// backend/server.js
require('dotenv').config();
const express = require('express');
const cors = require('cors');
const jwt = require('jsonwebtoken');
const { OAuth2Client } = require('google-auth-library');

const app = express();
const port = process.env.PORT || 5000;

app.use(cors());
app.use(express.json());

const CLIENT_ID = process.env.GOOGLE_CLIENT_ID;
const jwtSecret = process.env.JWT_SECRET || 'your-super-secret-key'; // Store securely!

const client = new OAuth2Client(CLIENT_ID);

// In-memory user database (replace with a real database in production)
const users = {};

// Function to generate a JWT
const generateToken = (payload) => {
  return jwt.sign(payload, jwtSecret, { expiresIn: '1h' }); // Adjust expiry as needed
};

// Middleware to verify JWT (for protected routes)
const verifyToken = (req, res, next) => {
  const authHeader = req.headers.authorization;
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.sendStatus(401);
  }

  jwt.verify(token, jwtSecret, (err, user) => {
    if (err) {
      return res.sendStatus(403);
    }
    req.user = user;
    next();
  });
};

// Google Sign-in endpoint
app.post('/auth/google', async (req, res) => {
  const { tokenId } = req.body;

  try {
    const ticket = await client.verifyIdToken({
      idToken: tokenId,
      audience: CLIENT_ID,
    });
    const payload = ticket.getPayload();
    const userId = payload.sub;

    // Check if user exists, create if not
    if (!users[userId]) {
      users[userId] = {
        id: userId,
        name: payload.name,
        email: payload.email,
        picture: payload.picture,
        // Add other relevant profile information
      };
    }

    // Generate JWT
    const token = generateToken({ userId: userId });

    res.json({ token });
  } catch (error) {
    console.error('Error verifying Google token:', error);
    res.status(401).json({ error: 'Invalid Google token' });
  }
});

// Protected profile endpoint
app.get('/profile', verifyToken, (req, res) => {
  const userId = req.user.userId;
  const userProfile = users[userId];

  if (userProfile) {
    res.json(userProfile);
  } else {
    res.status(404).json({ message: 'User not found' });
  }
});

app.listen(port, () => {
  console.log(`Server listening on port ${port}`);
});
```

