// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { GoogleLogin } from 'react-google-login';
import axios from 'axios';
import './App.css';

const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
const backendUrl = 'http://localhost:5000'; // Replace with your backend URL

function App() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check for token in localStorage on initial load
    const token = localStorage.getItem('authToken');
    if (token) {
      verifyAndFetchProfile(token);
    }
  }, []);

  const verifyAndFetchProfile = async (token) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${backendUrl}/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(response.data);
      setUser(true); // Indicate user is logged in
    } catch (error) {
      console.error('Error fetching profile:', error);
      localStorage.removeItem('authToken'); // Token might be invalid
      setUser(false);
      setError('Failed to fetch profile. Please log in again.');
    } finally {
      setLoading(false);
    }
  };

  const onSuccess = async (res) => {
    console.log('Login Success: currentUser:', res.profileObj);
    try {
      const response = await axios.post(`${backendUrl}/auth/google`, {
        tokenId: res.tokenId,
      });
      const token = response.data.token;
      localStorage.setItem('authToken', token); // Store the token
      verifyAndFetchProfile(token);
    } catch (error) {
      console.error('Error during Google sign-in:', error);
      setError('Google sign-in failed.');
    }
  };

  const onFailure = (res) => {
    console.log('Login failed: res:', res);
    setError('Google sign-in failed.');
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    setUser(false);
    setProfile(null);
  };

  return (
    <div className="App">
      <h1>Google Sign-in</h1>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      {!user && (
        <div>
          <p>Please sign in to view your profile.</p>
          <GoogleLogin
            clientId={clientId}
            buttonText="Sign in with Google"
            onSuccess={onSuccess}
            onFailure={onFailure}
            cookiePolicy={'single_host_origin'}
          />
        </div>
      )}

      {user && profile && (
        <div>
          <h2>Your Profile</h2>
          <img src={profile.picture} alt="Profile" />
          <p>Name: {profile.name}</p>
          <p>Email: {profile.email}</p>
          <button onClick={logout}>Logout</button>
        </div>
      )}

      {!user && !loading && !error && <p>You are logged out.</p>}
    </div>
  );
}

export default App;
```

